function openTab(n,t,i,r){for(var f,e=document.getElementById(i).querySelectorAll(r),u=0;u<e.length;u++)e[u].style.display="none";for(f=document.getElementById(i).querySelectorAll(".tab-link"),u=0;u<f.length;u++)f[u].className=f[u].className.replace(" tab-active","");document.getElementById(t).style.display="block";n.currentTarget.className+=" tab-active"}